import axios from "axios";
import { APIAddress } from "../../config";

import {
  FETCH_BY_ID_ERROR,
  FETCH_BY_ID_REQUEST,
  FETCH_BY_ID_SUCCESS,
} from "../types/byId";

export const fetchByIdRequest = () => {
  return {
    type: FETCH_BY_ID_REQUEST,
  };
};

export const fetchByIdSuccess = (byId) => {
  return {
    type: FETCH_BY_ID_SUCCESS,
    paylaod: byId,
  };
};

export const fetchByIdError = (error) => {
  return {
    type: FETCH_BY_ID_ERROR,
    payload: error,
  };
};

export const fetchById = (id) => {
  return (dispatch) => {
    dispatch(fetchByIdRequest);
    axios
      .get(`${APIAddress}` + id)
      .then((response) => {
        const byId = response.data.foundById;
        dispatch(fetchByIdSuccess(byId));
      })
      .catch((error) => {
        const errorMsg = error.message;
        dispatch(fetchByIdError(errorMsg));
      });
  };
};
